import styles from './styles.module.css';

function App() {
  return (
    <section className={styles.wrapper}>
      <div className={styles.container}>
        <section className={styles.header}>
          <div
            style={{
              display: 'flex',
              width: '50px',
              height: '50px',
              backgroundColor: 'red',
              textAlign: 'center',
              alignItems: 'center',
            }}
          >
            LOGO
          </div>
          <p>Text</p>
        </section>
        <div
          style={{
            display: 'flex',
            flex: 0.68,
          }}
        >
          <section className={styles.sidebar}>Sidebar</section>
          <section className={styles.body}>body</section>
        </div>
        <section className={styles.footer}>footer</section>
      </div>
    </section>
  );
}

export default App;

