import React, {
  useRef,
  useState,
  useEffect,
  forwardRef,
  useImperativeHandle,
} from 'react';
import styles from './style.module.css';

// ==================================================
// 1. BASIC DOM REFERENCE EXAMPLE
// ==================================================
const DomRefDemo: React.FC = () => {
  const inputRef = useRef<HTMLInputElement>(null);
  const [inputValue, setInputValue] = useState('');

  const focusInput = () => {
    inputRef.current?.focus();
  };

  const clearInput = () => {
    if (inputRef.current) {
      inputRef.current.value = '';
      setInputValue('');
      inputRef.current.focus();
    }
  };

  const selectAllText = () => {
    inputRef.current?.select();
  };

  return (
    <div className={styles.demo}>
      <h3>🎯 DOM Reference Demo</h3>
      <p>Direct access to DOM elements without causing re-renders</p>

      <div className={styles.inputGroup}>
        <input
          ref={inputRef}
          type='text'
          placeholder='Type something here...'
          className={styles.input}
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
        />
        <div className={styles.buttonGroup}>
          <button onClick={focusInput} className={styles.focusBtn}>
            Focus Input
          </button>
          <button onClick={selectAllText} className={styles.selectBtn}>
            Select All
          </button>
          <button onClick={clearInput} className={styles.clearBtn}>
            Clear & Focus
          </button>
        </div>
      </div>

      <div className={styles.info}>
        <p>
          <strong>Current value:</strong> {inputValue || 'Empty'}
        </p>
        <p>
          <strong>Use case:</strong> Direct DOM manipulation without state
          updates
        </p>
      </div>
    </div>
  );
};

// ==================================================
// 2. MUTABLE VALUES DEMO
// ==================================================
const MutableValueDemo: React.FC = () => {
  const renderCountRef = useRef(0);
  const [count, setCount] = useState(0);
  const [name, setName] = useState('');

  // Increment render count on every render
  renderCountRef.current += 1;

  return (
    <div className={styles.demo}>
      <h3>🔄 Mutable Values Demo</h3>
      <p>
        Store values that persist between renders without causing re-renders
      </p>

      <div className={styles.stats}>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>Component Renders:</span>
          <span className={styles.statValue}>{renderCountRef.current}</span>
        </div>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>State Counter:</span>
          <span className={styles.statValue}>{count}</span>
        </div>
      </div>

      <div className={styles.controls}>
        <button
          onClick={() => setCount(c => c + 1)}
          className={styles.incrementBtn}
        >
          Increment Counter (causes re-render)
        </button>

        <input
          type='text'
          placeholder='Type to see render count change'
          value={name}
          onChange={e => setName(e.target.value)}
          className={styles.input}
        />
      </div>

      <div className={styles.explanation}>
        <p>
          <strong>Notice:</strong> The render count increases every time the
          component re-renders, but changing the render count itself doesn't
          trigger a re-render.
        </p>
      </div>
    </div>
  );
};

// ==================================================
// 3. PREVIOUS VALUE TRACKING
// ==================================================
const PreviousValueDemo: React.FC = () => {
  const [count, setCount] = useState(0);
  const prevCountRef = useRef<number | undefined>(undefined);

  useEffect(() => {
    prevCountRef.current = count;
  }, [count]);

  const prevCount = prevCountRef.current;

  return (
    <div className={styles.demo}>
      <h3>⏮️ Previous Value Tracking</h3>
      <p>Keep track of previous values using useRef and useEffect</p>

      <div className={styles.valueDisplay}>
        <div className={styles.currentValue}>
          <span>Current: </span>
          <strong className={styles.current}>{count}</strong>
        </div>
        <div className={styles.previousValue}>
          <span>Previous: </span>
          <strong className={styles.previous}>
            {prevCount !== undefined ? prevCount : 'None'}
          </strong>
        </div>
        <div className={styles.difference}>
          <span>Difference: </span>
          <strong className={styles.diff}>
            {prevCount !== undefined ? count - prevCount : 'N/A'}
          </strong>
        </div>
      </div>

      <div className={styles.buttonGroup}>
        <button
          onClick={() => setCount(c => c + 1)}
          className={styles.incrementBtn}
        >
          +1
        </button>
        <button
          onClick={() => setCount(c => c + 5)}
          className={styles.incrementBtn}
        >
          +5
        </button>
        <button
          onClick={() => setCount(c => c - 1)}
          className={styles.decrementBtn}
        >
          -1
        </button>
        <button onClick={() => setCount(0)} className={styles.resetBtn}>
          Reset
        </button>
      </div>
    </div>
  );
};

// ==================================================
// 4. TIMER AND INTERVAL DEMO
// ==================================================
const TimerDemo: React.FC = () => {
  const [seconds, setSeconds] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  const startTimer = () => {
    if (!isRunning) {
      setIsRunning(true);
      intervalRef.current = setInterval(() => {
        setSeconds(s => s + 1);
      }, 1000);
    }
  };

  const stopTimer = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
      setIsRunning(false);
    }
  };

  const resetTimer = () => {
    stopTimer();
    setSeconds(0);
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  const formatTime = (totalSeconds: number) => {
    const mins = Math.floor(totalSeconds / 60);
    const secs = totalSeconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className={styles.demo}>
      <h3>⏱️ Timer & Interval Demo</h3>
      <p>Using useRef to store timer references and prevent memory leaks</p>

      <div className={styles.timerDisplay}>
        <div className={styles.timeValue}>{formatTime(seconds)}</div>
        <div className={styles.timerStatus}>
          Status:{' '}
          <span className={isRunning ? styles.running : styles.stopped}>
            {isRunning ? 'Running' : 'Stopped'}
          </span>
        </div>
      </div>

      <div className={styles.buttonGroup}>
        <button
          onClick={startTimer}
          disabled={isRunning}
          className={styles.startBtn}
        >
          Start
        </button>
        <button
          onClick={stopTimer}
          disabled={!isRunning}
          className={styles.stopBtn}
        >
          Stop
        </button>
        <button onClick={resetTimer} className={styles.resetBtn}>
          Reset
        </button>
      </div>

      <div className={styles.timerNote}>
        <p>
          <strong>Key Point:</strong> Using useRef to store interval ID prevents
          it from being lost during re-renders and ensures proper cleanup.
        </p>
      </div>
    </div>
  );
};

// ==================================================
// 5. SCROLL TO ELEMENT DEMO
// ==================================================
const ScrollDemo: React.FC = () => {
  const topRef = useRef<HTMLDivElement>(null);
  const middleRef = useRef<HTMLDivElement>(null);
  const bottomRef = useRef<HTMLDivElement>(null);

  const scrollToElement = (
    ref: React.RefObject<HTMLDivElement | null>,
    behavior: 'smooth' | 'auto' = 'smooth'
  ) => {
    ref.current?.scrollIntoView({ behavior, block: 'center' });
  };

  return (
    <div className={styles.demo}>
      <h3>📜 Scroll to Element Demo</h3>
      <p>Navigate to specific elements using refs and scrollIntoView</p>

      <div className={styles.scrollControls}>
        <button
          onClick={() => scrollToElement(topRef)}
          className={styles.scrollBtn}
        >
          Go to Top Section
        </button>
        <button
          onClick={() => scrollToElement(middleRef)}
          className={styles.scrollBtn}
        >
          Go to Middle Section
        </button>
        <button
          onClick={() => scrollToElement(bottomRef)}
          className={styles.scrollBtn}
        >
          Go to Bottom Section
        </button>
      </div>

      <div className={styles.scrollContent}>
        <div ref={topRef} className={styles.scrollSection}>
          <h4>🔝 Top Section</h4>
          <p>
            This is the top section. Lorem ipsum dolor sit amet, consectetur
            adipiscing elit. Sed do eiusmod tempor incididunt ut labore et
            dolore magna aliqua.
          </p>
        </div>

        <div className={styles.scrollSpacer}></div>

        <div ref={middleRef} className={styles.scrollSection}>
          <h4>🎯 Middle Section</h4>
          <p>
            This is the middle section. Ut enim ad minim veniam, quis nostrud
            exercitation ullamco laboris nisi ut aliquip ex ea commodo
            consequat.
          </p>
        </div>

        <div className={styles.scrollSpacer}></div>

        <div ref={bottomRef} className={styles.scrollSection}>
          <h4>🔽 Bottom Section</h4>
          <p>
            This is the bottom section. Duis aute irure dolor in reprehenderit
            in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
          </p>
        </div>
      </div>
    </div>
  );
};

// ==================================================
// 6. MEASURING ELEMENTS DEMO
// ==================================================
const MeasuringDemo: React.FC = () => {
  const elementRef = useRef<HTMLDivElement>(null);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const [content, setContent] = useState('Resize me by adding text!');

  const measureElement = () => {
    if (elementRef.current) {
      const { offsetWidth, offsetHeight } = elementRef.current;
      setDimensions({ width: offsetWidth, height: offsetHeight });
    }
  };

  useEffect(() => {
    measureElement();
  }, [content]);

  return (
    <div className={styles.demo}>
      <h3>📏 Element Measuring Demo</h3>
      <p>Measure element dimensions dynamically using refs</p>

      <div className={styles.measureControls}>
        <textarea
          value={content}
          onChange={e => setContent(e.target.value)}
          className={styles.contentInput}
          placeholder='Type to resize the measured element...'
        />
        <button onClick={measureElement} className={styles.measureBtn}>
          Measure Now
        </button>
      </div>

      <div ref={elementRef} className={styles.measuredElement}>
        <p>{content}</p>
      </div>

      <div className={styles.dimensions}>
        <div className={styles.dimension}>
          <span>Width: </span>
          <strong>{dimensions.width}px</strong>
        </div>
        <div className={styles.dimension}>
          <span>Height: </span>
          <strong>{dimensions.height}px</strong>
        </div>
      </div>
    </div>
  );
};

// ==================================================
// 7. FORWARDING REFS DEMO
// ==================================================
interface CustomInputProps {
  placeholder?: string;
  label: string;
  onFocus?: () => void;
}

const CustomInput = forwardRef<HTMLInputElement, CustomInputProps>(
  ({ placeholder, label, onFocus }, ref) => {
    return (
      <div className={styles.customInputWrapper}>
        <label className={styles.customLabel}>{label}</label>
        <input
          ref={ref}
          type='text'
          placeholder={placeholder}
          className={styles.customInput}
          onFocus={onFocus}
        />
      </div>
    );
  }
);

CustomInput.displayName = 'CustomInput';

const ForwardRefDemo: React.FC = () => {
  const customInputRef = useRef<HTMLInputElement>(null);
  const [focusCount, setFocusCount] = useState(0);

  const focusCustomInput = () => {
    customInputRef.current?.focus();
  };

  const handleInputFocus = () => {
    setFocusCount(c => c + 1);
  };

  return (
    <div className={styles.demo}>
      <h3>🔗 Forwarding Refs Demo</h3>
      <p>Using forwardRef to pass refs through custom components</p>

      <CustomInput
        ref={customInputRef}
        label='Custom Input Component'
        placeholder='This input can be focused from parent'
        onFocus={handleInputFocus}
      />

      <div className={styles.forwardRefControls}>
        <button onClick={focusCustomInput} className={styles.focusBtn}>
          Focus Custom Input
        </button>
        <div className={styles.focusCounter}>
          Focus count: <strong>{focusCount}</strong>
        </div>
      </div>

      <div className={styles.codeExample}>
        <pre>{`const CustomInput = forwardRef<HTMLInputElement, Props>(
  ({ placeholder, label }, ref) => {
    return (
      <input
        ref={ref}
        type="text"
        placeholder={placeholder}
      />
    );
  }
);`}</pre>
      </div>
    </div>
  );
};

// ==================================================
// 8. IMPERATIVE HANDLE DEMO
// ==================================================
interface VideoPlayerHandle {
  play: () => void;
  pause: () => void;
  reset: () => void;
}

interface VideoPlayerProps {
  src?: string;
}

const VideoPlayer = forwardRef<VideoPlayerHandle, VideoPlayerProps>(
  (props, ref) => {
    const videoRef = useRef<HTMLVideoElement>(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);

    useImperativeHandle(ref, () => ({
      play: () => {
        videoRef.current?.play();
        setIsPlaying(true);
      },
      pause: () => {
        videoRef.current?.pause();
        setIsPlaying(false);
      },
      reset: () => {
        if (videoRef.current) {
          videoRef.current.currentTime = 0;
          setCurrentTime(0);
          setIsPlaying(false);
        }
      },
    }));

    const handleTimeUpdate = () => {
      if (videoRef.current) {
        setCurrentTime(videoRef.current.currentTime);
      }
    };

    return (
      <div className={styles.videoPlayer}>
        <div className={styles.mockVideo}>
          <div className={styles.mockVideoContent}>
            <div className={isPlaying ? styles.pauseIcon : styles.playIcon}>
              {isPlaying ? '⏸️' : '▶️'}
            </div>
            <div className={styles.videoInfo}>
              <div>Status: {isPlaying ? 'Playing' : 'Paused'}</div>
              <div>Time: {currentTime.toFixed(1)}s</div>
            </div>
          </div>
        </div>
        {/* Hidden actual video for demo */}
        <video
          ref={videoRef}
          style={{ display: 'none' }}
          onTimeUpdate={handleTimeUpdate}
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
        >
          <source src='data:video/mp4;base64,AAAAAA' type='video/mp4' />
        </video>
      </div>
    );
  }
);

VideoPlayer.displayName = 'VideoPlayer';

const ImperativeHandleDemo: React.FC = () => {
  const playerRef = useRef<VideoPlayerHandle>(null);

  return (
    <div className={styles.demo}>
      <h3>🎮 Imperative Handle Demo</h3>
      <p>
        Expose custom methods from child components using useImperativeHandle
      </p>

      <VideoPlayer ref={playerRef} />

      <div className={styles.playerControls}>
        <button
          onClick={() => playerRef.current?.play()}
          className={styles.playBtn}
        >
          Play
        </button>
        <button
          onClick={() => playerRef.current?.pause()}
          className={styles.pauseBtn}
        >
          Pause
        </button>
        <button
          onClick={() => playerRef.current?.reset()}
          className={styles.resetBtn}
        >
          Reset
        </button>
      </div>

      <div className={styles.note}>
        <p>
          <strong>Note:</strong> useImperativeHandle lets you customize the
          instance value that is exposed to parent components when using ref.
        </p>
      </div>
    </div>
  );
};

// ==================================================
// 9. BEST PRACTICES SECTION
// ==================================================
const BestPractices: React.FC = () => {
  return (
    <div className={styles.bestPractices}>
      <h3>📋 useRef Best Practices</h3>
      <div className={styles.practiceGrid}>
        <div className={styles.practiceItem}>
          <h4>✅ Use for DOM Access</h4>
          <p>
            Perfect for focusing inputs, scrolling to elements, or measuring
            dimensions without triggering re-renders.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Store Mutable Values</h4>
          <p>
            Keep values that need to persist between renders but shouldn't cause
            re-renders when changed.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Clean Up Side Effects</h4>
          <p>
            Store timer IDs, event listeners, or subscriptions for proper
            cleanup in useEffect.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Previous Value Tracking</h4>
          <p>
            Combine with useEffect to keep track of previous state or prop
            values.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>❌ Don't Use for State</h4>
          <p>
            If changing the value should trigger a re-render, use useState
            instead of useRef.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>❌ Don't Read During Render</h4>
          <p>
            Avoid reading ref.current during rendering. Use useEffect or event
            handlers instead.
          </p>
        </div>
      </div>
    </div>
  );
};

// ==================================================
// 10. MAIN COMPONENT
// ==================================================
const UseRef: React.FC = () => {
  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1>🎯 useRef Hook Tutorial</h1>
        <p>
          Master React's useRef hook with practical examples and best practices
        </p>
      </header>

      <main className={styles.main}>
        <section className={styles.section}>
          <h2>Interactive Examples</h2>
          <div className={styles.demoGrid}>
            <DomRefDemo />
            <MutableValueDemo />
            <PreviousValueDemo />
            <TimerDemo />
            <ScrollDemo />
            <MeasuringDemo />
            <ForwardRefDemo />
            <ImperativeHandleDemo />
          </div>
        </section>

        <BestPractices />

        <section className={styles.section}>
          <h2>📚 What You've Learned</h2>
          <div className={styles.learningGrid}>
            <div className={styles.learningItem}>
              <span className={styles.learningIcon}>🎯</span>
              <h3>DOM References</h3>
              <p>
                Direct access to DOM elements for focus, selection, and
                manipulation
              </p>
            </div>
            <div className={styles.learningItem}>
              <span className={styles.learningIcon}>🔄</span>
              <h3>Mutable Values</h3>
              <p>
                Store values that persist between renders without causing
                re-renders
              </p>
            </div>
            <div className={styles.learningItem}>
              <span className={styles.learningIcon}>⏱️</span>
              <h3>Timers & Cleanup</h3>
              <p>Manage intervals and timeouts with proper cleanup</p>
            </div>
            <div className={styles.learningItem}>
              <span className={styles.learningIcon}>🔗</span>
              <h3>Ref Forwarding</h3>
              <p>Pass refs through custom components using forwardRef</p>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default UseRef;
