// import { useState } from 'react';
import styles from './styles.module.css';
// import { ActivityIndicator } from './Components/Loader';

function App() {
  return (
    <>
      <section className={styles.wrapper}>
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'red',
          }}
        >
          Header
        </div>
        <div style={{ display: 'flex', flex: 1, backgroundColor: 'green' }}>
          <div
            style={{ display: 'flex', flex: 0.2, backgroundColor: 'orange' }}
          >
            Body 1
          </div>
          <div style={{ display: 'flex', flex: 0.8, backgroundColor: 'blue' }}>
            Body 2
          </div>
        </div>
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'teal',
            justifyContent: 'center',
          }}
        >
          Footer
        </div>
      </section>
    </>
  );
}

export default App;

