// import { useState } from 'react';
import styles from './styles.module.css';
// import { ActivityIndicator } from './Components/Loader';
import logo from './assets/images/logo.svg';
import './common.css';

function App() {
  return (
    <div>
      <section className={styles.wrapper}>
        <div
          className={styles.headerContainer}
          // style={{
          //   display: 'flex',
          //   flex: 0.3,
          //   backgroundColor: 'white',
          //   justifyContent: 'space-between',
          //   padding: '0px 16px',
          // }}
        >
          <div
            style={{
              backgroundColor: 'red',
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-start',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                display: 'flex',
                // backgroundColor: 'orange',
                width: '50px',
                height: '50px',
                marginRight: '2px',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <img src={logo} className={styles.logo} alt='Logo' />
            </div>
            <div style={{ fontSize: '24px', fontWeight: 'bold' }}>Kanchoko</div>
          </div>
          {/* <div
            style={{ backgroundColor: 'violet', display: 'flex', flex: 0.4 }}
          >
            B
          </div> */}
          <div
            style={{
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-end',
              alignItems: 'center',
            }}
          >
            <button
              className={styles.loginButton}
              onClick={() => {
                console.log('Logged in');
              }}
            >
              Log in
            </button>
            <button
              className={styles.logOutButton}
              onClick={() => {
                console.log('Logged out');
              }}
            >
              Log out
            </button>
          </div>
        </div>
        {/* Body */}
        <div style={{ display: 'flex', flex: 1, backgroundColor: 'green' }}>
          <div className={styles.sideBar}>Body 1</div>
          <div style={{ display: 'flex', flex: 0.8, backgroundColor: 'blue' }}>
            Body 2
          </div>
        </div>
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'teal',
            justifyContent: 'center',
          }}
        >
          Footer
        </div>
      </section>
    </div>
  );
}

export default App;

