import { Link, Route, Routes } from 'react-router-dom';
import { Layout } from './components/Layout';
import './global.css';
import { useAppTheme } from './hooks/useAppTheme';
import Atom from './pages/Atom';
import Mike from './pages/Mike';

interface PageInfo {
  path: string;
  title: string;
  description: string;
  emoji: string;
}

const pages: PageInfo[] = [
  {
    path: '/mike',
    title: 'Mike Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: 'üè¢',
  },
  {
    path: '/atom',
    title: 'Atom Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: '‚öõÔ∏è',
  },
];

function HomePage() {
  const { theme, toggleTheme } = useAppTheme();

  return (
    <div className="home-container">
      <header className="home-header">
        <div className="home-header-content">
          <h1>üöÄ React App</h1>
          <p>Choose a page to navigate to:</p>
        </div>
        <button
          onClick={toggleTheme}
          className="btn btn-theme"
          title={`Switch to ${theme === 'light' ? 'Dark' : 'Light'} mode`}
          aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} theme`}
        >
          <span aria-hidden="true">{theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}</span>
          {theme === 'light' ? 'Dark' : 'Light'}
        </button>
      </header>

      <div className="card-grid">
        {pages.map((page, index) => (
          <Link
            key={page.path}
            to={page.path}
            className="page-card"
            aria-label={`Navigate to ${page.title}`}
          >
            <div className="page-card-emoji" aria-hidden="true">
              {page.emoji}
            </div>
            <h2 className="page-card-title">{page.title}</h2>
            <p className="page-card-description">{page.description}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}

function App() {
  return (
    <Layout>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/mike" element={<Mike />} />
        <Route path="/atom" element={<Atom />} />
      </Routes>
    </Layout>
  );
}

export default App;

