import { useState } from 'react';
import './App.css';
import Mike from './Pages/Mike';
import Atom from './Pages/Atom';

// Define the available pages
type PageType =
  | 'home'
  | 'basicCss'
  | 'contextProvider'
  | 'useRef'
  | 'counters'
  | 'responsive'
  | 'mike'
  | 'atom';

interface PageInfo {
  id: PageType;
  title: string;
  description: string;
  emoji: string;
}

const pages: PageInfo[] = [
  {
    id: 'basicCss',
    title: 'Basic CSS Tutorial',
    description:
      'Learn Traditional, Global, Responsive, PostCSS & Module CSS with interactive examples',
    emoji: 'üé®',
  },
  {
    id: 'contextProvider',
    title: 'Context API Tutorial',
    description:
      'Master React Context API and useReducer for global state management with practical examples',
    emoji: 'üîÑ',
  },
  {
    id: 'useRef',
    title: 'useRef Hook Tutorial',
    description:
      'Learn React useRef hook for DOM access, mutable values, timers, and ref forwarding with hands-on examples',
    emoji: 'üéØ',
  },
  {
    id: 'counters',
    title: 'React Counters',
    description:
      'Interactive counter components demonstrating React hooks and state management',
    emoji: 'üî¢',
  },
  {
    id: 'responsive',
    title: 'Responsive Layout',
    description:
      'A basic responsive layout example using CSS Modules and media queries',
    emoji: 'üì±',
  },
  {
    id: 'mike',
    title: 'Mike Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: 'üè¢',
  },
  {
    id: 'atom',
    title: 'Atom Page',
    description:
      'Simple component demonstration page',
    emoji: '‚öõÔ∏è',
  },
];

function App() {
  const [currentPage, setCurrentPage] = useState<PageType>('home');
  const [theme, setTheme] = useState<string>('light');
  const [counter, setCounter] = useState<number>(0);

  const toggleTheme = () => {
    setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  };

  const incrementCounter = () => {
    setCounter(prev => prev + 1);
  };

  const decrementCounter = () => {
    setCounter(prev => prev - 1);
  };

  const navigateToPage = (pageId: PageType) => {
    setCurrentPage(pageId);
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'basicCss':
        return <BasicCss />;
      case 'contextProvider':
        return <ContextProvider />;
      case 'useRef':
        return <UseRef />;
      case 'mike':
        return <Mike />;
      case 'atom':
        return <Atom />;
      case 'counters':
        return (
          <div className='counters-page'>
            <div className='page-header'>
              <h1>üî¢ React Counters Demo</h1>
              <p>Interactive examples of React hooks and state management</p>
            </div>
            <div className='counters-container'>
              <div className='counter-controls'>
                <h3>Global Counter: {counter}</h3>
                <div className='button-group'>
                  <button
                    className='counter-btn increase'
                    onClick={incrementCounter}
                  >
                    ‚ûï Increase
                  </button>
                  <button
                    className='counter-btn decrease'
                    onClick={decrementCounter}
                  >
                    ‚ûñ Decrease
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      case 'responsive':
        return <ResponsivePage />;
      default:
        return (
          <div className='home-page'>
            <header className='app-header'>
              <h1>üöÄ React Learning Hub</h1>
              <p className='app-subtitle'>
                Explore interactive examples and tutorials to master React and
                modern web development
              </p>
            </header>

            <div className='pages-grid'>
              {pages.map(page => (
                <div
                  key={page.id}
                  className='page-card'
                  onClick={() => navigateToPage(page.id)}
                  role='button'
                  tabIndex={0}
                  onKeyDown={e => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      navigateToPage(page.id);
                    }
                  }}
                >
                  <div className='page-emoji'>{page.emoji}</div>
                  <h2 className='page-title'>{page.title}</h2>
                  <p className='page-description'>{page.description}</p>
                  <div className='page-arrow'>‚Üí</div>
                </div>
              ))}
            </div>

            <div className='features-section'>
              <h2>üìö What You'll Learn</h2>
              <div className='features-grid'>
                <div className='feature-item'>
                  <span className='feature-icon'>üé®</span>
                  <h3>Modern CSS</h3>
                  <p>
                    CSS Modules, PostCSS, Responsive Design, and best practices
                  </p>
                </div>
                <div className='feature-item'>
                  <span className='feature-icon'>‚öõÔ∏è</span>
                  <h3>React Fundamentals</h3>
                  <p>
                    Hooks, State Management, Components, and modern patterns
                  </p>
                </div>
                <div className='feature-item'>
                  <span className='feature-icon'>üì±</span>
                  <h3>Responsive Design</h3>
                  <p>
                    Mobile-first approach, breakpoints, and adaptive layouts
                  </p>
                </div>
                <div className='feature-item'>
                  <span className='feature-icon'>üõ†Ô∏è</span>
                  <h3>Best Practices</h3>
                  <p>
                    Code organization, naming conventions, and maintainable
                    styles
                  </p>
                </div>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className={`app ${theme}`}>
      {currentPage !== 'home' && (
        <nav className='app-nav'>
          <button
            className='nav-back'
            onClick={() => setCurrentPage('home')}
            title='Back to Home'
          >
            ‚Üê Back to Home
          </button>
          <div className='nav-title'>
            {pages.find(p => p.id === currentPage)?.title || 'Page'}
          </div>
          <button
            className='theme-toggle-nav'
            onClick={toggleTheme}
            title={`Switch to ${theme === 'light' ? 'Dark' : 'Light'} Mode`}
          >
            {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          </button>
        </nav>
      )}

      <main className='app-main'>{renderCurrentPage()}</main>

      {currentPage === 'home' && (
        <footer className='app-footer'>
          <div className='footer-content'>
            <p>Built with ‚ù§Ô∏è for learning React & Modern CSS</p>
            <button className='theme-toggle-footer' onClick={toggleTheme}>
              {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}{' '}
              {theme === 'light' ? 'Dark' : 'Light'} Mode
            </button>
          </div>
        </footer>
      )}
    </div>
  );
}

export default App;

/**
 * 1. Basic CSS: Mike: ‚úÖ, Atom: ‚ö†Ô∏è
 * 2. Basic React ‚úÖ
 * 3. Flexbox: Mike: ‚úÖ, Atom: ‚ö†Ô∏è
 * 4. HOC concept => Higher Order Component
 * 4. React Hooks: useState, useEffect, useMemo, useCallback, ‚úÖ    useRef
 * 5. Custom Hooks ‚úÖ
 * 6. Context API ‚úÖ
 * 8. TypeScript: Mike
 **/
