import React, { useState, useEffect } from 'react';

interface ContactPageProps {
  theme: string;
}

interface ContactForm {
  name: string;
  email: string;
  subject: string;
  message: string;
  newsletter: boolean;
  favoriteLanguage: string;
}

interface SavedMessage {
  id: number;
  name: string;
  subject: string;
  timestamp: string;
}

const useLocalStorage = <T,>(key: string, initialValue: T) => {
  const [storedValue, setStoredValue] = useState<T>(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.log(error);
      return initialValue;
    }
  });

  const setValue = (value: T | ((val: T) => T)) => {
    try {
      const valueToStore =
        value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      console.log(error);
    }
  };

  return [storedValue, setValue] as const;
};

const ContactPage: React.FC<ContactPageProps> = ({ theme }) => {
  // Form state
  const [formData, setFormData] = useState<ContactForm>({
    name: '',
    email: '',
    subject: '',
    message: '',
    newsletter: false,
    favoriteLanguage: 'javascript',
  });

  // Form validation state
  const [errors, setErrors] = useState<Partial<ContactForm>>({});
  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
  const [submitSuccess, setSubmitSuccess] = useState<boolean>(false);

  // Saved messages using custom hook
  const [savedMessages, setSavedMessages] = useLocalStorage<SavedMessage[]>(
    'contactMessages',
    []
  );

  // Character count state
  const [messageLength, setMessageLength] = useState<number>(0);
  const maxMessageLength = 500;

  // Update message length when message changes
  useEffect(() => {
    setMessageLength(formData.message.length);
  }, [formData.message]);

  // Handle input changes
  const handleInputChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement
    >
  ) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));

    // Clear error when user starts typing
    if (errors[name as keyof ContactForm]) {
      setErrors(prev => ({
        ...prev,
        [name]: undefined,
      }));
    }
  };

  // Validate form
  const validateForm = (): boolean => {
    const newErrors: Partial<ContactForm> = {};

    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }
    if (!formData.subject.trim()) newErrors.subject = 'Subject is required';
    if (!formData.message.trim()) newErrors.message = 'Message is required';
    if (formData.message.length > maxMessageLength) {
      newErrors.message = `Message must be ${maxMessageLength} characters or less`;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Save to localStorage
    const newMessage: SavedMessage = {
      id: Date.now(),
      name: formData.name,
      subject: formData.subject,
      timestamp: new Date().toLocaleString(),
    };

    setSavedMessages(prev => [newMessage, ...prev].slice(0, 10)); // Keep only last 10

    setIsSubmitting(false);
    setSubmitSuccess(true);

    // Reset form
    setFormData({
      name: '',
      email: '',
      subject: '',
      message: '',
      newsletter: false,
      favoriteLanguage: 'javascript',
    });

    // Hide success message after 3 seconds
    setTimeout(() => setSubmitSuccess(false), 3000);
  };

  // Clear all saved messages
  const clearSavedMessages = () => {
    setSavedMessages([]);
  };

  // Programming languages for the select dropdown
  const programmingLanguages = [
    { value: 'javascript', label: 'JavaScript' },
    { value: 'python', label: 'Python' },
    { value: 'java', label: 'Java' },
    { value: 'csharp', label: 'C#' },
    { value: 'php', label: 'PHP' },
    { value: 'ruby', label: 'Ruby' },
    { value: 'go', label: 'Go' },
    { value: 'rust', label: 'Rust' },
  ];

  return (
    <div className='page-container fade-in'>
      <h1 className='page-title'>üìû Contact Us</h1>
      <p className='page-subtitle'>
        This page demonstrates form handling, validation, and local storage!
      </p>

      {/* Success Message */}
      {submitSuccess && (
        <div
          className='info-card'
          style={{
            backgroundColor: 'rgba(16, 172, 132, 0.2)',
            borderLeftColor: '#10ac84',
          }}
        >
          <h3>‚úÖ Message Sent Successfully!</h3>
          <p>Thank you for your message. We'll get back to you soon!</p>
        </div>
      )}

      {/* Contact Form */}
      <div className='interactive-section'>
        <h3>üìù Contact Form</h3>
        <p>
          Fill out this form to see form validation and state management in
          action!
        </p>

        <form onSubmit={handleSubmit} style={{ marginTop: '1rem' }}>
          {/* Name Field */}
          <div className='input-group'>
            <label htmlFor='name'>Name *</label>
            <input
              id='name'
              name='name'
              type='text'
              value={formData.name}
              onChange={handleInputChange}
              placeholder='Enter your full name'
              style={{
                borderColor: errors.name
                  ? '#ff6b6b'
                  : 'rgba(255, 255, 255, 0.3)',
              }}
            />
            {errors.name && (
              <p
                style={{
                  color: '#ff6b6b',
                  fontSize: '0.9rem',
                  marginTop: '0.5rem',
                }}
              >
                {errors.name}
              </p>
            )}
          </div>

          {/* Email Field */}
          <div className='input-group'>
            <label htmlFor='email'>Email *</label>
            <input
              id='email'
              name='email'
              type='email'
              value={formData.email}
              onChange={handleInputChange}
              placeholder='Enter your email address'
              style={{
                borderColor: errors.email
                  ? '#ff6b6b'
                  : 'rgba(255, 255, 255, 0.3)',
              }}
            />
            {errors.email && (
              <p
                style={{
                  color: '#ff6b6b',
                  fontSize: '0.9rem',
                  marginTop: '0.5rem',
                }}
              >
                {errors.email}
              </p>
            )}
          </div>

          {/* Subject Field */}
          <div className='input-group'>
            <label htmlFor='subject'>Subject *</label>
            <input
              id='subject'
              name='subject'
              type='text'
              value={formData.subject}
              onChange={handleInputChange}
              placeholder="What's this about?"
              style={{
                borderColor: errors.subject
                  ? '#ff6b6b'
                  : 'rgba(255, 255, 255, 0.3)',
              }}
            />
            {errors.subject && (
              <p
                style={{
                  color: '#ff6b6b',
                  fontSize: '0.9rem',
                  marginTop: '0.5rem',
                }}
              >
                {errors.subject}
              </p>
            )}
          </div>

          {/* Favorite Programming Language */}
          <div className='input-group'>
            <label htmlFor='favoriteLanguage'>
              Favorite Programming Language
            </label>
            <select
              id='favoriteLanguage'
              name='favoriteLanguage'
              value={formData.favoriteLanguage}
              onChange={handleInputChange}
              style={{
                padding: '0.8rem',
                borderRadius: '10px',
                border: '2px solid rgba(255, 255, 255, 0.3)',
                background: 'rgba(255, 255, 255, 0.1)',
                color: 'inherit',
                width: '100%',
              }}
            >
              {programmingLanguages.map(lang => (
                <option key={lang.value} value={lang.value}>
                  {lang.label}
                </option>
              ))}
            </select>
          </div>

          {/* Message Field */}
          <div className='input-group'>
            <label htmlFor='message'>
              Message *
              <span
                style={{
                  fontSize: '0.9rem',
                  color:
                    messageLength > maxMessageLength ? '#ff6b6b' : '#48cae4',
                  marginLeft: '1rem',
                }}
              >
                ({messageLength}/{maxMessageLength} characters)
              </span>
            </label>
            <textarea
              id='message'
              name='message'
              value={formData.message}
              onChange={handleInputChange}
              placeholder='Tell us about your React learning journey...'
              rows={5}
              style={{
                borderColor: errors.message
                  ? '#ff6b6b'
                  : 'rgba(255, 255, 255, 0.3)',
                resize: 'vertical',
              }}
            />
            {errors.message && (
              <p
                style={{
                  color: '#ff6b6b',
                  fontSize: '0.9rem',
                  marginTop: '0.5rem',
                }}
              >
                {errors.message}
              </p>
            )}
          </div>

          {/* Newsletter Checkbox */}
          <div className='input-group'>
            <label
              style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
            >
              <input
                type='checkbox'
                name='newsletter'
                checked={formData.newsletter}
                onChange={handleInputChange}
                style={{ transform: 'scale(1.2)' }}
              />
              Subscribe to our React learning newsletter
            </label>
          </div>

          {/* Submit Button */}
          <button
            type='submit'
            className='interactive-btn'
            disabled={isSubmitting}
            style={{
              width: '100%',
              padding: '1rem',
              fontSize: '1.1rem',
              opacity: isSubmitting ? 0.7 : 1,
              cursor: isSubmitting ? 'not-allowed' : 'pointer',
            }}
          >
            {isSubmitting ? 'üîÑ Sending...' : 'üì§ Send Message'}
          </button>
        </form>
      </div>

      {/* Form Data Preview */}
      <div className='interactive-section'>
        <h3>üìä Live Form Data Preview</h3>
        <p>
          See how your form data changes in real-time (this demonstrates state
          management):
        </p>
        <div className='info-card'>
          <pre
            style={{
              fontSize: '0.9rem',
              overflow: 'auto',
              backgroundColor: 'rgba(0, 0, 0, 0.3)',
              padding: '1rem',
              borderRadius: '8px',
            }}
          >
            {JSON.stringify(formData, null, 2)}
          </pre>
        </div>
      </div>

      {/* Saved Messages */}
      {savedMessages.length > 0 && (
        <div className='interactive-section'>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '1rem',
            }}
          >
            <h3>üíæ Previously Saved Messages</h3>
            <button
              className='interactive-btn'
              onClick={clearSavedMessages}
              style={{ fontSize: '0.9rem', padding: '0.5rem 1rem' }}
            >
              üóëÔ∏è Clear All
            </button>
          </div>
          <p>
            These are stored in your browser's localStorage (demonstrates data
            persistence):
          </p>

          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            {savedMessages.map(message => (
              <div
                key={message.id}
                className='info-card'
                style={{ margin: '0.5rem 0' }}
              >
                <div
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                  }}
                >
                  <h4>{message.subject}</h4>
                  <span style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                    {message.timestamp}
                  </span>
                </div>
                <p>
                  <strong>From:</strong> {message.name}
                </p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* React Concepts */}
      <div className='feature-grid'>
        <div className='feature-item'>
          <div className='feature-icon'>‚úÖ</div>
          <h4>Form Validation</h4>
          <p>Real-time validation with error messages and visual feedback.</p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>üíæ</div>
          <h4>Local Storage</h4>
          <p>Persisting data in the browser using localStorage API.</p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>üîÑ</div>
          <h4>Custom Hooks</h4>
          <p>Reusable logic with custom React hooks like useLocalStorage.</p>
        </div>
      </div>
    </div>
  );
};

export default ContactPage;
