import React, {
  createContext,
  useContext,
  useState,
  useReducer,
  ReactNode,
} from 'react';
import styles from './style.module.css';

// ==================================================
// 1. THEME CONTEXT EXAMPLE
// ==================================================
interface ThemeContextType {
  theme: 'light' | 'dark' | 'blue';
  setTheme: (theme: 'light' | 'dark' | 'blue') => void;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

const ThemeProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [theme, setTheme] = useState<'light' | 'dark' | 'blue'>('light');

  const toggleTheme = () => {
    setTheme(prev => {
      switch (prev) {
        case 'light':
          return 'dark';
        case 'dark':
          return 'blue';
        case 'blue':
          return 'light';
        default:
          return 'light';
      }
    });
  };

  return (
    <ThemeContext.Provider value={{ theme, setTheme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

const useTheme = () => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

// ==================================================
// 2. USER CONTEXT EXAMPLE
// ==================================================
interface User {
  id: number;
  name: string;
  email: string;
  role: 'admin' | 'user' | 'guest';
}

interface UserContextType {
  user: User | null;
  login: (user: User) => void;
  logout: () => void;
  updateUser: (updates: Partial<User>) => void;
}

const UserContext = createContext<UserContextType | undefined>(undefined);

const UserProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);

  const login = (userData: User) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const updateUser = (updates: Partial<User>) => {
    if (user) {
      const updatedUser = { ...user, ...updates };
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
    }
  };

  return (
    <UserContext.Provider value={{ user, login, logout, updateUser }}>
      {children}
    </UserContext.Provider>
  );
};

const useUser = () => {
  const context = useContext(UserContext);
  if (context === undefined) {
    throw new Error('useUser must be used within a UserProvider');
  }
  return context;
};

// ==================================================
// 3. SHOPPING CART CONTEXT WITH REDUCER
// ==================================================
interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

interface CartState {
  items: CartItem[];
  total: number;
}

type CartAction =
  | { type: 'ADD_ITEM'; payload: Omit<CartItem, 'quantity'> }
  | { type: 'REMOVE_ITEM'; payload: number }
  | { type: 'UPDATE_QUANTITY'; payload: { id: number; quantity: number } }
  | { type: 'CLEAR_CART' };

const cartReducer = (state: CartState, action: CartAction): CartState => {
  switch (action.type) {
    case 'ADD_ITEM': {
      const existingItem = state.items.find(
        item => item.id === action.payload.id
      );
      let newItems: CartItem[];

      if (existingItem) {
        newItems = state.items.map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        newItems = [...state.items, { ...action.payload, quantity: 1 }];
      }

      const total = newItems.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
      );
      return { items: newItems, total };
    }

    case 'REMOVE_ITEM': {
      const newItems = state.items.filter(item => item.id !== action.payload);
      const total = newItems.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
      );
      return { items: newItems, total };
    }

    case 'UPDATE_QUANTITY': {
      const newItems = state.items
        .map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: Math.max(0, action.payload.quantity) }
            : item
        )
        .filter(item => item.quantity > 0);

      const total = newItems.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
      );
      return { items: newItems, total };
    }

    case 'CLEAR_CART':
      return { items: [], total: 0 };

    default:
      return state;
  }
};

interface CartContextType {
  state: CartState;
  addItem: (item: Omit<CartItem, 'quantity'>) => void;
  removeItem: (id: number) => void;
  updateQuantity: (id: number, quantity: number) => void;
  clearCart: () => void;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

const CartProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, { items: [], total: 0 });

  const addItem = (item: Omit<CartItem, 'quantity'>) => {
    dispatch({ type: 'ADD_ITEM', payload: item });
  };

  const removeItem = (id: number) => {
    dispatch({ type: 'REMOVE_ITEM', payload: id });
  };

  const updateQuantity = (id: number, quantity: number) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { id, quantity } });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  return (
    <CartContext.Provider
      value={{ state, addItem, removeItem, updateQuantity, clearCart }}
    >
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

// ==================================================
// 4. COMPONENT EXAMPLES
// ==================================================

// Theme Component
const ThemeDemo: React.FC = () => {
  const { theme, setTheme, toggleTheme } = useTheme();

  return (
    <div className={`${styles.demo} ${styles[theme]}`}>
      <h3>🎨 Theme Context Demo</h3>
      <p>
        Current theme: <strong>{theme}</strong>
      </p>
      <div className={styles.buttonGroup}>
        <button onClick={() => setTheme('light')} className={styles.themeBtn}>
          ☀️ Light
        </button>
        <button onClick={() => setTheme('dark')} className={styles.themeBtn}>
          🌙 Dark
        </button>
        <button onClick={() => setTheme('blue')} className={styles.themeBtn}>
          💙 Blue
        </button>
        <button onClick={toggleTheme} className={styles.toggleBtn}>
          🔄 Toggle Theme
        </button>
      </div>
    </div>
  );
};

// User Component
const UserDemo: React.FC = () => {
  const { user, login, logout, updateUser } = useUser();

  const handleLogin = () => {
    const sampleUsers = [
      {
        id: 1,
        name: 'John Doe',
        email: 'john@example.com',
        role: 'admin' as const,
      },
      {
        id: 2,
        name: 'Jane Smith',
        email: 'jane@example.com',
        role: 'user' as const,
      },
      {
        id: 3,
        name: 'Guest User',
        email: 'guest@example.com',
        role: 'guest' as const,
      },
    ];

    const randomUser =
      sampleUsers[Math.floor(Math.random() * sampleUsers.length)];
    login(randomUser);
  };

  return (
    <div className={styles.demo}>
      <h3>👤 User Context Demo</h3>
      {user ? (
        <div className={styles.userInfo}>
          <h4>Welcome, {user.name}!</h4>
          <p>
            <strong>Email:</strong> {user.email}
          </p>
          <p>
            <strong>Role:</strong>{' '}
            <span className={styles[user.role]}>{user.role}</span>
          </p>
          <div className={styles.buttonGroup}>
            <button
              onClick={() => updateUser({ name: user.name + ' (Updated)' })}
              className={styles.updateBtn}
            >
              Update Name
            </button>
            <button onClick={logout} className={styles.logoutBtn}>
              Logout
            </button>
          </div>
        </div>
      ) : (
        <div>
          <p>No user logged in</p>
          <button onClick={handleLogin} className={styles.loginBtn}>
            Random Login
          </button>
        </div>
      )}
    </div>
  );
};

// Shopping Cart Component
const CartDemo: React.FC = () => {
  const { state, addItem, removeItem, updateQuantity, clearCart } = useCart();

  const sampleProducts = [
    { id: 1, name: 'Laptop', price: 999.99 },
    { id: 2, name: 'Mouse', price: 29.99 },
    { id: 3, name: 'Keyboard', price: 79.99 },
    { id: 4, name: 'Monitor', price: 299.99 },
  ];

  return (
    <div className={styles.demo}>
      <h3>🛒 Cart Context Demo (with useReducer)</h3>

      <div className={styles.products}>
        <h4>Products:</h4>
        <div className={styles.productGrid}>
          {sampleProducts.map(product => (
            <div key={product.id} className={styles.product}>
              <span>{product.name}</span>
              <span>${product.price}</span>
              <button
                onClick={() => addItem(product)}
                className={styles.addBtn}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className={styles.cart}>
        <div className={styles.cartHeader}>
          <h4>Cart ({state.items.length} items)</h4>
          <span className={styles.total}>Total: ${state.total.toFixed(2)}</span>
        </div>

        {state.items.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <>
            {state.items.map(item => (
              <div key={item.id} className={styles.cartItem}>
                <span>{item.name}</span>
                <div className={styles.quantity}>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className={styles.quantityBtn}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className={styles.quantityBtn}
                  >
                    +
                  </button>
                </div>
                <span>${(item.price * item.quantity).toFixed(2)}</span>
                <button
                  onClick={() => removeItem(item.id)}
                  className={styles.removeBtn}
                >
                  🗑️
                </button>
              </div>
            ))}
            <button onClick={clearCart} className={styles.clearBtn}>
              Clear Cart
            </button>
          </>
        )}
      </div>
    </div>
  );
};

// Best Practices Component
const BestPractices: React.FC = () => {
  return (
    <div className={styles.bestPractices}>
      <h3>📋 Context API Best Practices</h3>
      <div className={styles.practiceGrid}>
        <div className={styles.practiceItem}>
          <h4>✅ Create Custom Hooks</h4>
          <p>
            Always create custom hooks (like useTheme, useUser) to consume
            context. This provides better error handling and type safety.
          </p>
          <pre className={styles.codeExample}>{`const useTheme = () => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within ThemeProvider');
  }
  return context;
};`}</pre>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Split Contexts by Concern</h4>
          <p>
            Don't put everything in one context. Separate contexts by logical
            domains (theme, user, cart).
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Use useReducer for Complex State</h4>
          <p>
            For complex state logic with multiple sub-values or when the next
            state depends on the previous one, use useReducer.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>✅ Provide Default Values</h4>
          <p>
            Always provide meaningful default values and proper TypeScript types
            for your contexts.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>❌ Don't Overuse Context</h4>
          <p>
            Context is not a replacement for all prop drilling. Use it for truly
            global state like themes, auth, etc.
          </p>
        </div>

        <div className={styles.practiceItem}>
          <h4>❌ Avoid Large Context Objects</h4>
          <p>
            Large context objects can cause unnecessary re-renders. Split them
            into smaller, focused contexts.
          </p>
        </div>
      </div>
    </div>
  );
};

// ==================================================
// 5. MAIN COMPONENT
// ==================================================
const ContextProvider: React.FC = () => {
  return (
    <ThemeProvider>
      <UserProvider>
        <CartProvider>
          <div className={styles.container}>
            <header className={styles.header}>
              <h1>🔄 React Context API Tutorial</h1>
              <p>
                Learn how to manage global state with React Context and
                useReducer
              </p>
            </header>

            <main className={styles.main}>
              <section className={styles.section}>
                <h2>Interactive Examples</h2>
                <div className={styles.demoGrid}>
                  <ThemeDemo />
                  <UserDemo />
                  <CartDemo />
                </div>
              </section>

              <BestPractices />

              <section className={styles.section}>
                <h2>📚 What You've Learned</h2>
                <div className={styles.learningGrid}>
                  <div className={styles.learningItem}>
                    <span className={styles.learningIcon}>🎨</span>
                    <h3>Theme Context</h3>
                    <p>Simple state management with useState in context</p>
                  </div>
                  <div className={styles.learningItem}>
                    <span className={styles.learningIcon}>👤</span>
                    <h3>User Context</h3>
                    <p>Authentication state with localStorage persistence</p>
                  </div>
                  <div className={styles.learningItem}>
                    <span className={styles.learningIcon}>🛒</span>
                    <h3>Cart Context</h3>
                    <p>Complex state management with useReducer</p>
                  </div>
                  <div className={styles.learningItem}>
                    <span className={styles.learningIcon}>🔧</span>
                    <h3>Best Practices</h3>
                    <p>Custom hooks, error handling, and proper patterns</p>
                  </div>
                </div>
              </section>
            </main>
          </div>
        </CartProvider>
      </UserProvider>
    </ThemeProvider>
  );
};

export default ContextProvider;
