    console.log('Log Inside ComponentB:');
    return <div>Component B</div>;
  };

  // Memo function
  // function a () {}, const a = () => {}
  const ComponentC = useCallback(() => {
    console.log('Log Inside ComponentC:');
    return <div>Component C</div>;
  }, []);

  // Memo value
