import { useEffect } from 'react';
import {
  Link,
  Route,
  Routes,
  useLocation,
  useNavigate,
} from 'react-router-dom';
import './global.css';
import { useAppTheme } from './hooks/useAppTheme';
import Atom from './pages/Atom';
import Mike from './pages/Mike';

interface PageInfo {
  path: string;
  title: string;
  description: string;
  emoji: string;
}

const pages: PageInfo[] = [
  {
    path: '/mike',
    title: 'Mike Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: '🏢',
  },
  {
    path: '/atom',
    title: 'Atom Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: '⚛️',
  },
];

function HomePage() {
  const { theme, setTheme } = useAppTheme();

  const toggleTheme = () => {
    setTheme((prev: string) => (prev === 'light' ? 'dark' : 'light'));
  };

  return (
    <div className="home-container">
      <header className="home-header">
        <div className="home-header-content">
          <h1>🚀 React App</h1>
          <p>Choose a page to navigate to:</p>
        </div>
        <button
          onClick={toggleTheme}
          className="btn btn-theme"
          title={`Switch to ${theme === 'light' ? 'Dark' : 'Light'} mode`}
          aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} theme`}
        >
          <span aria-hidden="true">{theme === 'light' ? '🌙' : '☀️'}</span>
          {theme === 'light' ? 'Dark' : 'Light'}
        </button>
      </header>

      <div className="card-grid">
        {pages.map((page, index) => (
          <Link
            key={page.path}
            to={page.path}
            className="page-card"
            aria-label={`Navigate to ${page.title}`}
          >
            <div className="page-card-emoji" aria-hidden="true">
              {page.emoji}
            </div>
            <h2 className="page-card-title">{page.title}</h2>
            <p className="page-card-description">{page.description}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}

function Layout({ children }: { children: React.ReactNode }) {
  const { theme, setTheme } = useAppTheme();
  const location = useLocation();
  const navigate = useNavigate();
  const isHome = location.pathname === '/';

  const toggleTheme = () => {
    setTheme((prev: string) => (prev === 'light' ? 'dark' : 'light'));
  };

  return (
    <div className="app" data-theme={theme}>
      {!isHome && (
        <nav className="nav" role="navigation" aria-label="Main navigation">
          <button
            onClick={() => navigate('/')}
            className="btn btn-back"
            aria-label="Back to home page"
          >
            <span aria-hidden="true">←</span>
            Back to Home
          </button>
          <button
            onClick={toggleTheme}
            className="btn btn-theme"
            title={`Switch to ${theme === 'light' ? 'Dark' : 'Light'} mode`}
            aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} theme`}
          >
            <span aria-hidden="true">{theme === 'light' ? '🌙' : '☀️'}</span>
            <span className="sr-only">
              {theme === 'light' ? 'Dark' : 'Light'} mode
            </span>
          </button>
        </nav>
      )}

      <main role="main">{children}</main>
    </div>
  );
}

function App() {
  const { theme } = useAppTheme();

  // Apply theme to document root for CSS variables
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  return (
    <Layout>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/mike" element={<Mike />} />
        <Route path="/atom" element={<Atom />} />
      </Routes>
    </Layout>
  );
}

export default App;

/**
 * 1. Basic CSS: Mike: ✅, Atom: ⚠️
 * 2. Basic React ✅
 * 3. Flexbox: Mike: ✅, Atom: ⚠️
 * 4. HOC concept => Higher Order Component
 * 4. React Hooks: useState, useEffect, useMemo, useCallback, ✅    useRef
 * 5. Custom Hooks ✅
 * 6. Context API ✅
 * 8. TypeScript: Mike
 **/
