import { useState } from 'react';
import BasicCss from './Pages/BasicCss';
import ContextProvider from './Pages/ContextProvider';
import UseRef from './Pages/UseRef';
import ResponsivePage from './Pages/Responsive';
import { CounterA, CounterB } from './Components/CounterA';
import './App.css';

// Define the available pages
type PageType =
  | 'home'
  | 'basicCss'
  | 'contextProvider'
  | 'useRef'
  | 'counters'
  | 'responsive';

interface PageInfo {
  id: PageType;
  title: string;
  description: string;
  emoji: string;
}

const pages: PageInfo[] = [
  {
    id: 'basicCss',
    title: 'Basic CSS Tutorial',
    description:
      'Learn Traditional, Global, Responsive, PostCSS & Module CSS with interactive examples',
    emoji: 'üé®',
  },
  {
    id: 'contextProvider',
    title: 'Context API Tutorial',
    description:
      'Master React Context API and useReducer for global state management with practical examples',
    emoji: 'üîÑ',
  },
  {
    id: 'useRef',
    title: 'useRef Hook Tutorial',
    description:
      'Learn React useRef hook for DOM access, mutable values, timers, and ref forwarding with hands-on examples',
    emoji: 'üéØ',
  },
  {
    id: 'counters',
    title: 'React Counters',
    description:
      'Interactive counter components demonstrating React hooks and state management',
    emoji: 'üî¢',
  },
  {
    id: 'responsive',
    title: 'Responsive Layout',
    description:
      'A basic responsive layout example using CSS Modules and media queries',
    emoji: 'üì±',
  },
];

function App() {
  return (
    <div className={`app ${theme}`}>
      {currentPage !== 'home' && (
        <nav className='app-nav'>
          <button
            className='nav-back'
            onClick={() => setCurrentPage('home')}
            title='Back to Home'
          >
            ‚Üê Back to Home
          </button>
          <div className='nav-title'>
            {pages.find(p => p.id === currentPage)?.title || 'Page'}
          </div>
          <button
            className='theme-toggle-nav'
            onClick={toggleTheme}
            title={`Switch to ${theme === 'light' ? 'Dark' : 'Light'} Mode`}
          >
            {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          </button>
        </nav>
      )}

      <main className='app-main'>{renderCurrentPage()}</main>

      {currentPage === 'home' && (
        <footer className='app-footer'>
          <div className='footer-content'>
            <p>Built with ‚ù§Ô∏è for learning React & Modern CSS</p>
            <button className='theme-toggle-footer' onClick={toggleTheme}>
              {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}{' '}
              {theme === 'light' ? 'Dark' : 'Light'} Mode
            </button>
          </div>
        </footer>
      )}
    </div>
  );
}

export default App;

