import { useState } from 'react';
import styles from './styles.module.css';
import { ActivityIndicator } from './Components/Loader';

function App() {
  const [username, setUsername] = useState<string>('User Name');
  const [isLoading, setLoading] = useState<boolean>(false);

  async function login() {
    console.log('login');
    setLoading(true);

    setTimeout(() => {
      setUsername('mike');
      setLoading(false);
    }, 3000);
  }

  const logout = async () => {
    console.log('logout');
    setLoading(true);

    setTimeout(() => {
      setUsername('User Name');
      setLoading(false);
    }, 3000);
  };

  console.log('Welcome Mike');

  return (
    <section className={styles.wrapper}>
      <div className={styles.container}>
        {/* {isLoading && <ActivityIndicator />} */}
        {isLoading ? (
          <ActivityIndicator />
        ) : (
          <section className={styles.header}>
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
              }}
            >
              <div className={styles.logo}>LOGO</div>
              <p
                style={{
                  backgroundColor: 'aqua',
                  fontSize: 20,
                  fontWeight: 'bold',
                }}
              >
                こんにちはママ
              </p>
            </div>
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
              }}
            >
              <p
                style={{
                  marginRight: 16,
                }}
              >
                Welcome: {username}
              </p>
              <button className={styles.loginButton} onClick={login}>
                Login
              </button>
              <button className={styles.logoutButton} onClick={logout}>
                Logout
              </button>
            </div>
          </section>
        )}

        <div className={styles.bodyWrapper}>
          <section className={styles.sidebar}>Sidebar</section>
          <section className={styles.body}>body</section>
        </div>
        <section className={styles.footer}>footer</section>
      </div>
    </section>
  );
}

export default App;

