import React, { useState } from 'react';

interface AboutPageProps {
  theme: string;
}

interface TodoItem {
  id: number;
  text: string;
  completed: boolean;
  priority: 'low' | 'medium' | 'high';
}

const LearningProgress: React.FC<{ theme: string }> = ({ theme }) => {
  const [skills, setSkills] = useState([
    { name: 'HTML', level: 85 },
    { name: 'CSS', level: 70 },
    { name: 'JavaScript', level: 60 },
    { name: 'React', level: 45 },
    { name: 'TypeScript', level: 30 },
  ]);

  const updateSkillLevel = (index: number, change: number) => {
    setSkills(prevSkills =>
      prevSkills.map((skill, i) =>
        i === index
          ? {
              ...skill,
              level: Math.max(0, Math.min(100, skill.level + change)),
            }
          : skill
      )
    );
  };

  return (
    <div className='interactive-section'>
      <h3>📈 Learning Progress Tracker</h3>
      <p>Track your skills and see how state arrays work!</p>
      {skills.map((skill, index) => (
        <div key={skill.name} style={{ marginBottom: '1rem' }}>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '0.5rem',
            }}
          >
            <span style={{ fontWeight: 'bold' }}>{skill.name}</span>
            <span>{skill.level}%</span>
          </div>
          <div
            style={{
              width: '100%',
              height: '10px',
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              borderRadius: '5px',
              overflow: 'hidden',
            }}
          >
            <div
              style={{
                width: `${skill.level}%`,
                height: '100%',
                backgroundColor:
                  skill.level > 70
                    ? '#10ac84'
                    : skill.level > 40
                      ? '#feca57'
                      : '#ff6b6b',
                transition: 'width 0.3s ease',
                borderRadius: '5px',
              }}
            />
          </div>
          <div className='button-group' style={{ marginTop: '0.5rem' }}>
            <button
              className='interactive-btn'
              onClick={() => updateSkillLevel(index, -10)}
              disabled={skill.level <= 0}
              style={{ fontSize: '0.8rem', padding: '0.3rem 0.8rem' }}
            >
              -10%
            </button>
            <button
              className='interactive-btn'
              onClick={() => updateSkillLevel(index, 10)}
              disabled={skill.level >= 100}
              style={{ fontSize: '0.8rem', padding: '0.3rem 0.8rem' }}
            >
              +10%
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

const AboutPage: React.FC<AboutPageProps> = ({ theme }) => {
  const [todos, setTodos] = useState<TodoItem[]>([
    { id: 1, text: 'Learn React basics', completed: true, priority: 'high' },
    {
      id: 2,
      text: 'Understand props and state',
      completed: false,
      priority: 'high',
    },
    {
      id: 3,
      text: 'Practice CSS styling',
      completed: false,
      priority: 'medium',
    },
  ]);

  const [newTodoText, setNewTodoText] = useState<string>('');
  const [newTodoPriority, setNewTodoPriority] = useState<
    'low' | 'medium' | 'high'
  >('medium');

  const [showStats, setShowStats] = useState<boolean>(false);

  const addTodo = () => {
    if (newTodoText.trim()) {
      const newTodo: TodoItem = {
        id: Date.now(),
        text: newTodoText,
        completed: false,
        priority: newTodoPriority,
      };
      setTodos([...todos, newTodo]);
      setNewTodoText('');
    }
  };

  const toggleTodo = (id: number) => {
    setTodos(
      todos.map(todo =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const deleteTodo = (id: number) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const completedTodos = todos.filter(todo => todo.completed).length;
  const totalTodos = todos.length;
  const completionPercentage =
    totalTodos > 0 ? Math.round((completedTodos / totalTodos) * 100) : 0;

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return '#ff6b6b';
      case 'medium':
        return '#feca57';
      case 'low':
        return '#48cae4';
      default:
        return '#ffffff';
    }
  };

  return (
    <div className='page-container fade-in'>
      <h1 className='page-title'>ℹ️ About This App</h1>
      <p className='page-subtitle'>
        Learn advanced React concepts with interactive examples!
      </p>

      <div className='info-card'>
        <h3>🎯 What You'll Learn Here</h3>
        <p>
          This app demonstrates essential React concepts in an interactive way:
        </p>
        <ul style={{ marginLeft: '2rem', marginTop: '1rem' }}>
          <li>
            <strong>State Management:</strong> Using useState to handle data
            that changes
          </li>
          <li>
            <strong>Props:</strong> Passing data between parent and child
            components
          </li>
          <li>
            <strong>Event Handling:</strong> Responding to user interactions
          </li>
          <li>
            <strong>CSS Styling:</strong> Making your app look professional
          </li>
          <li>
            <strong>Array Operations:</strong> Working with lists of data
          </li>
          <li>
            <strong>Conditional Rendering:</strong> Showing/hiding content
            dynamically
          </li>
        </ul>
      </div>

      <div className='interactive-section'>
        <h3>📝 Interactive Todo List</h3>
        <p>This demonstrates array state management and CRUD operations!</p>

        <div
          style={{
            display: 'flex',
            gap: '1rem',
            marginBottom: '1rem',
            flexWrap: 'wrap',
          }}
        >
          <div className='input-group' style={{ flex: 1, minWidth: '200px' }}>
            <input
              type='text'
              value={newTodoText}
              onChange={e => setNewTodoText(e.target.value)}
              placeholder='Enter a new learning goal...'
              onKeyPress={e => e.key === 'Enter' && addTodo()}
            />
          </div>
          <select
            value={newTodoPriority}
            onChange={e =>
              setNewTodoPriority(e.target.value as 'low' | 'medium' | 'high')
            }
            style={{
              padding: '0.8rem',
              borderRadius: '10px',
              border: '2px solid rgba(255, 255, 255, 0.3)',
              background: 'rgba(255, 255, 255, 0.1)',
              color: 'inherit',
            }}
          >
            <option value='low'>Low Priority</option>
            <option value='medium'>Medium Priority</option>
            <option value='high'>High Priority</option>
          </select>
          <button className='interactive-btn' onClick={addTodo}>
            ➕ Add Goal
          </button>
        </div>

        {/* Todo list */}
        <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
          {todos.map(todo => (
            <div
              key={todo.id}
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '1rem',
                margin: '0.5rem 0',
                background: todo.completed
                  ? 'rgba(16, 172, 132, 0.2)'
                  : 'rgba(255, 255, 255, 0.1)',
                borderRadius: '10px',
                borderLeft: `4px solid ${getPriorityColor(todo.priority)}`,
                textDecoration: todo.completed ? 'line-through' : 'none',
                opacity: todo.completed ? 0.7 : 1,
                transition: 'all 0.3s ease',
              }}
            >
              <div
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  flex: 1,
                }}
              >
                <input
                  type='checkbox'
                  checked={todo.completed}
                  onChange={() => toggleTodo(todo.id)}
                  style={{ transform: 'scale(1.2)' }}
                />
                <span style={{ flex: 1 }}>{todo.text}</span>
                <span
                  style={{
                    fontSize: '0.8rem',
                    padding: '0.2rem 0.5rem',
                    borderRadius: '10px',
                    backgroundColor: getPriorityColor(todo.priority),
                    color: 'white',
                    fontWeight: 'bold',
                  }}
                >
                  {todo.priority.toUpperCase()}
                </span>
              </div>
              <button
                onClick={() => deleteTodo(todo.id)}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#ff6b6b',
                  cursor: 'pointer',
                  fontSize: '1.2rem',
                  padding: '0.5rem',
                }}
              >
                🗑️
              </button>
            </div>
          ))}
        </div>

        {/* Statistics */}
        <div style={{ marginTop: '1rem' }}>
          <button
            className='interactive-btn'
            onClick={() => setShowStats(!showStats)}
          >
            {showStats ? '📊 Hide' : '📈 Show'} Statistics
          </button>

          {showStats && (
            <div className='info-card' style={{ marginTop: '1rem' }}>
              <h3>📊 Your Progress</h3>
              <p>
                <strong>Total Goals:</strong> {totalTodos}
              </p>
              <p>
                <strong>Completed:</strong> {completedTodos}
              </p>
              <p>
                <strong>Completion Rate:</strong> {completionPercentage}%
              </p>
              <div
                style={{
                  width: '100%',
                  height: '20px',
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  borderRadius: '10px',
                  overflow: 'hidden',
                  marginTop: '1rem',
                }}
              >
                <div
                  style={{
                    width: `${completionPercentage}%`,
                    height: '100%',
                    backgroundColor:
                      completionPercentage > 70 ? '#10ac84' : '#feca57',
                    transition: 'width 0.3s ease',
                  }}
                />
              </div>
            </div>
          )}
        </div>
      </div>

      <LearningProgress theme={theme} />

      {/* Code Concepts */}
      <div className='feature-grid'>
        <div className='feature-item'>
          <div className='feature-icon'>🔄</div>
          <h4>Array State</h4>
          <p>
            Managing lists of data with useState and array methods like map,
            filter.
          </p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>📝</div>
          <h4>Forms</h4>
          <p>
            Handling user input with controlled components and event handlers.
          </p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>🔧</div>
          <h4>CRUD Operations</h4>
          <p>
            Create, Read, Update, Delete - the fundamental operations for data.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutPage;
