import { ReactNode } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { Button } from '../Button';
import { Loader } from '../Loader';

interface PageHeaderProps {
  title: string;
  subtitle?: string;
  actions?: ReactNode;
  className?: string;
  showAuthButton?: boolean;
}

export function PageHeader({
  title,
  subtitle,
  actions,
  className = '',
  showAuthButton = true,
}: PageHeaderProps) {
  const { user, isAuthenticated, isLoading, login, logout } = useAuth();

  const handleLogin = async () => {
    await login({
      username: 'emilys',
      password: 'emilyspass',
      expiresInMins: 30,
    });
  };

  const renderAuthButton = () => {
    if (isLoading) {
      return <Loader size="sm" text="Logging in..." />;
    }

    if (isAuthenticated && user) {
      return (
        <div className="auth-section">
          <span className="user-greeting">Welcome, {user.firstName}!</span>
          <Button variant="secondary" onClick={logout} size="sm">
            Logout
          </Button>
        </div>
      );
    }

    return (
      <Button variant="primary" onClick={handleLogin} size="sm">
        Login
      </Button>
    );
  };

  return (
    <header className={`home-header ${className}`}>
      <div className="home-header-content">
        <h1>{title}</h1>
        {subtitle && <p>{subtitle}</p>}
      </div>
      <div className="header-actions">
        {showAuthButton && renderAuthButton()}
        {actions}
      </div>
    </header>
  );
}
