import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './App.css';

import HomePage from './Pages/HomePage';
import ContactPage from './Pages/ContactPage';
import AboutPage from './Pages/AboutPage';
import CounterPage from './Pages/CounterPage';

interface NavigationProps {
  currentTheme: string;
  onThemeChange: () => void;
}

const Navigation: React.FC<NavigationProps> = ({
  currentTheme,
  onThemeChange,
}) => {
  return (
    <nav className={`navigation ${currentTheme}`}>
      <div className='nav-brand'>
        <h2>üöÄ React Learning App</h2>
      </div>
      <ul className='nav-links'>
        <li>
          <Link to='/'>üè† Home</Link>
        </li>
        <li>
          <Link to='/about'>‚ÑπÔ∏è About</Link>
        </li>
        <li>
          <Link to='/counter'>üî¢ Counter</Link>
        </li>
        <li>
          <Link to='/contact'>üìû Contact</Link>
        </li>
      </ul>
      <button className='theme-toggle' onClick={onThemeChange}>
        {currentTheme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        Switch to {currentTheme === 'light' ? 'Dark' : 'Light'} Mode
      </button>
    </nav>
  );
};

function App() {
  const [theme, setTheme] = useState<string>('light');

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  return (
    <div className={`app ${theme}`}>
      <BrowserRouter>
        <Navigation currentTheme={theme} onThemeChange={toggleTheme} />

        <main className='main-content'>
          <Routes>
            {/** Home Page */}
            <Route path='/' element={<HomePage theme={theme} />} />

            <Route path='/about' element={<AboutPage theme={theme} />} />
            <Route path='/counter' element={<CounterPage theme={theme} />} />
            <Route path='/contact' element={<ContactPage theme={theme} />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className={`footer ${theme}`}>
          <p>Built with ‚ù§Ô∏è for learning React ‚Ä¢ Current theme: {theme}</p>
        </footer>
      </BrowserRouter>
    </div>
  );
}

export default App;
