import { useState } from 'react';
import './global.css';
import Atom from './pages/Atom';
import Mike from './pages/Mike';

// Define the available pages
type PageType = 'home' | 'mike' | 'atom';

interface PageInfo {
  id: PageType;
  title: string;
  description: string;
  emoji: string;
}

const pages: PageInfo[] = [
  {
    id: 'mike',
    title: 'Mike Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: 'üè¢',
  },
  {
    id: 'atom',
    title: 'Atom Page',
    description:
      'Japanese auction platform with responsive design and CSS modules',
    emoji: '‚öõÔ∏è',
  },
];

function App() {
  const [currentPage, setCurrentPage] = useState<PageType>('home');

  const navigateToPage = (pageId: PageType) => {
    setCurrentPage(pageId);
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'mike':
        return <Mike />;
      case 'atom':
        return <Atom />;
      default:
        return (
          <div style={{ padding: '2rem', textAlign: 'center' }}>
            <header>
              <h1>üöÄ React App</h1>
              <p>Choose a page to navigate to:</p>
            </header>

            <div
              style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '2rem',
                marginTop: '2rem',
              }}
            >
              {pages.map(page => (
                <div
                  key={page.id}
                  onClick={() => navigateToPage(page.id)}
                  style={{
                    border: '2px solid #f78c23',
                    borderRadius: '12px',
                    padding: '2rem',
                    cursor: 'pointer',
                    transition: 'transform 0.2s ease',
                  }}
                  onMouseEnter={e => {
                    e.currentTarget.style.transform = 'translateY(-4px)';
                  }}
                  onMouseLeave={e => {
                    e.currentTarget.style.transform = 'translateY(0)';
                  }}
                >
                  <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>
                    {page.emoji}
                  </div>
                  <h2 style={{ margin: '0 0 1rem 0', color: '#f78c23' }}>
                    {page.title}
                  </h2>
                  <p style={{ margin: '0', color: '#666' }}>
                    {page.description}
                  </p>
                </div>
              ))}
            </div>
          </div>
        );
    }
  };

  return (
    <div>
      {currentPage !== 'home' && (
        <nav style={{ padding: '1rem', borderBottom: '1px solid #e2e1da' }}>
          <button
            onClick={() => setCurrentPage('home')}
            style={{
              background: 'none',
              border: '2px solid #f78c23',
              color: '#f78c23',
              padding: '0.5rem 1rem',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600',
            }}
          >
            ‚Üê Back to Home
          </button>
        </nav>
      )}

      <main>{renderCurrentPage()}</main>
    </div>
  );
}

export default App;

