import { useCallback, useEffect, useMemo, useState } from 'react';
import './App.css';

function App() {
  const [theme, setTheme] = useState<string>('light');
  const [counter, setCounter] = useState<number>(0);
  // const [estateProper, setEstateProper] = useState<Object | null>();

  const toggleTheme = () => {
    console.log('Log Inside toggleTheme');
    setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  };

  const incrementCounter = useCallback(() => {
    console.log('Log Inside incrementCounter');
    setCounter(prev => prev + 1);
  }, []);

  const decrementCounter = useCallback(() => {
    console.log('Log Inside decrementCounter');
    setCounter(prev => prev - 1);
  }, []);

  // const toggleTheme = useCallback(() => {
  //   console.log('Log Inside toggleTheme');
  //   setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  // }, []);

  console.log('Log Outside theme:', theme);

  useEffect(() => {
    console.log('Hello useEffect');
    // fetch data from API or perform side effects
  }, [theme]);

  // 1: Render State & Functions
  // 2: Render UI
  // 3: When state changes
  // 4: Re-render State & Functions => new strate

  const ComponentA = () => {
    console.log('Log Inside ComponentA:');
    return <div className={`component-a ${theme}`}>Component A</div>;
  };

  const ComponentB = () => {
    console.log('Log Inside ComponentB:');
    return <div>Component B</div>;
  };

  // Memo function
  // function a () {}, const a = () => {}
  const ComponentC = useCallback(({ data }: { data: string }) => {
    console.log('Log Inside ComponentC:', data);
    return <div>Component C</div>;
  }, []);

  // Memo value
  // const b = () => { a + b }, [a, b] = 3
  const ComponentD = useMemo(() => {
    console.log('Log Inside ComponentD:');
    return <div>Component D</div>;
  }, []);

  return (
    <>
      {<div>Counter: {counter}</div>}
      {/** Increase */}
      <button className='theme-toggle' onClick={toggleTheme}>
        {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
      </button>

      <button className='theme-toggle' onClick={toggleTheme}>
        {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
      </button>
      <ComponentA />
      <ComponentB />
      <ComponentC data={'Kid'} />
      {ComponentD}
      {/* <ComponentD /> */}
      <div>
        <button className='theme-toggle' onClick={toggleTheme}>
          {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
        </button>

        {/* Footer */}
        <footer className={`footer ${theme}`}>
          <p>Built with ‚ù§Ô∏è for learning React ‚Ä¢ Current theme: {theme}</p>
        </footer>
      </div>
    </>
  );
}

export default App;
