import React, { useState, useEffect } from 'react';

interface HomePageProps {
  theme: string;
}

interface CounterProps {
  title: string;
  initialValue: number;
  theme: string;
}

const Counter: React.FC<CounterProps> = ({ title, initialValue, theme }) => {
  const [count, setCount] = useState<number>(initialValue);

  return (
    <div className='interactive-section'>
      <h3>{title}</h3>
      <div className='counter-container'>
        <div className='counter-display'>{count}</div>
        <div className='button-group'>
          <button
            className='interactive-btn'
            onClick={() => setCount(count - 1)}
            disabled={count <= 0}
          >
            ‚ûñ Decrease
          </button>
          <button
            className='interactive-btn'
            onClick={() => setCount(initialValue)}
          >
            üîÑ Reset
          </button>
          <button
            className='interactive-btn'
            onClick={() => setCount(count + 1)}
          >
            ‚ûï Increase
          </button>
        </div>
      </div>
    </div>
  );
};

const HomePage: React.FC<HomePageProps> = ({ theme }) => {
  const [userName, setUserName] = useState<string>('');
  const [isVisible, setIsVisible] = useState<boolean>(true);
  const [selectedColor, setSelectedColor] = useState<string>('#ff6b6b');
  const [timeOfDay, setTimeOfDay] = useState<string>('');

  useEffect(() => {
    const hour = new Date().getHours();
    if (hour < 12) {
      setTimeOfDay('Good Morning');
    } else if (hour < 18) {
      setTimeOfDay('Good Afternoon');
    } else {
      setTimeOfDay('Good Evening');
    }
  }, []);

  const handleNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUserName(event.target.value);
  };

  const colors = ['#ff6b6b', '#feca57', '#48cae4', '#ff9ff3', '#54a0ff'];

  return (
    <div className='page-container fade-in'>
      <h1 className='page-title'>üè† Welcome Home!</h1>
      <p className='page-subtitle'>
        {timeOfDay}! This page demonstrates React state management, props, and
        CSS styling.
      </p>

      <div className='interactive-section'>
        <h3>üëã Personal Greeting</h3>
        <div className='input-group'>
          <label htmlFor='nameInput'>What's your name?</label>
          <input
            id='nameInput'
            type='text'
            value={userName}
            onChange={handleNameChange}
            placeholder='Enter your name here...'
          />
        </div>
        {userName && (
          <div className='info-card'>
            <h3>Hello, {userName}! üéâ</h3>
            <p>
              Welcome to our React learning app. You're currently using the{' '}
              {theme} theme!
            </p>
          </div>
        )}
      </div>

      <Counter title='üî¢ Basic Counter' initialValue={0} theme={theme} />
      <Counter
        title='üéØ Goal Counter (Start at 10)'
        initialValue={10}
        theme={theme}
      />

      <div className='interactive-section'>
        <h3>üé® Color Picker</h3>
        <p>Choose a color to see state changes:</p>
        <div className='button-group'>
          {colors.map((color, index) => (
            <button
              key={index}
              className='interactive-btn'
              style={{
                backgroundColor: color,
                border: selectedColor === color ? '3px solid white' : 'none',
              }}
              onClick={() => setSelectedColor(color)}
            >
              Color {index + 1}
            </button>
          ))}
        </div>
        <div
          className='info-card'
          style={{
            borderLeftColor: selectedColor,
            backgroundColor: `${selectedColor}20`, // Add transparency
          }}
        >
          <h3 style={{ color: selectedColor }}>Selected Color</h3>
          <p>
            You picked: <strong>{selectedColor}</strong>
          </p>
          <p>This demonstrates how state changes can update the UI!</p>
        </div>
      </div>

      <div className='interactive-section'>
        <h3>üëÅÔ∏è Show/Hide Content</h3>
        <button
          className='interactive-btn'
          onClick={() => setIsVisible(!isVisible)}
        >
          {isVisible ? 'üôà Hide' : 'üëÄ Show'} Secret Message
        </button>

        {isVisible && (
          <div className='info-card pulse'>
            <h3>üéä Congratulations!</h3>
            <p>You've successfully learned how to:</p>
            <ul style={{ marginLeft: '2rem', marginTop: '1rem' }}>
              <li>‚úÖ Use React state with useState</li>
              <li>‚úÖ Handle events (onClick, onChange)</li>
              <li>‚úÖ Pass props between components</li>
              <li>‚úÖ Apply conditional rendering</li>
              <li>‚úÖ Style components with CSS</li>
            </ul>
          </div>
        )}
      </div>

      <div className='feature-grid'>
        <div className='feature-item'>
          <div className='feature-icon'>‚öõÔ∏è</div>
          <h4>State</h4>
          <p>Data that changes over time and causes re-renders when updated.</p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>üì¶</div>
          <h4>Props</h4>
          <p>
            Data passed from parent to child components. Like function
            parameters!
          </p>
        </div>
        <div className='feature-item'>
          <div className='feature-icon'>üé®</div>
          <h4>CSS</h4>
          <p>Styles that make your app look beautiful and responsive.</p>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
