// import { useState } from 'react';
import styles from './styles.module.css';
// import { ActivityIndicator } from './Components/Loader';

function App() {
  return (
    <>
      <section className={styles.wrapper}>
        {/* Header */}
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'red',
            justifyContent: 'space-between',
            padding: '0px 16px',
          }}
        >
          <div
            style={{
              backgroundColor: 'white',
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-start',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: 'orange',
                width: '50px',
                height: '50px',
                marginRight: '8px',
              }}
            ></div>
            <div style={{}}>Kanchoko</div>
          </div>
          {/* <div
            style={{ backgroundColor: 'violet', display: 'flex', flex: 0.4 }}
          >
            B
          </div> */}
          <div
            style={{
              backgroundColor: 'beige',
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-end',
              alignItems: 'center',
            }}
          >
            <button
              style={{
                width: '100px',
                height: '50px',
                backgroundColor: 'orange',
                borderColor: 'orange',
                borderRadius: '5px',
                color: 'white',
              }}
              onClick={() => {
                console.log('Logged in');
              }}
            >
              Log in
            </button>
            <button
              style={{
                width: '100px',
                height: '50px',
                backgroundColor: 'white',
                borderRadius: '5px',
                color: 'orange',
              }}
              onClick={() => {
                console.log('Logged out');
              }}
            >
              Log out
            </button>
          </div>
        </div>
        {/* Body */}
        <div style={{ display: 'flex', flex: 1, backgroundColor: 'green' }}>
          <div
            style={{ display: 'flex', flex: 0.2, backgroundColor: 'orange' }}
          >
            Body 1
          </div>
          <div style={{ display: 'flex', flex: 0.8, backgroundColor: 'blue' }}>
            Body 2
          </div>
        </div>
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'teal',
            justifyContent: 'center',
          }}
        >
          Footer
        </div>
      </section>
    </>
  );
}

export default App;

