import React, { useState } from 'react';
import styles from './CounterPage.module.css';

interface CounterPageProps {
  theme: string;
}

const CounterPage: React.FC<CounterPageProps> = ({ theme }) => {
  const [count, setCount] = useState<number>(0);
  const [step, setStep] = useState<number>(1);
  const [history, setHistory] = useState<number[]>([0]);

  const increment = () => {
    const newCount = count + step;
    setCount(newCount);
    setHistory(prev => [...prev, newCount].slice(-10)); // Keep last 10
  };

  const decrement = () => {
    const newCount = count - step;
    setCount(newCount);
    setHistory(prev => [...prev, newCount].slice(-10));
  };

  const reset = () => {
    setCount(0);
    setHistory([0]);
  };

  const handleStepChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(e.target.value) || 1;
    setStep(Math.max(1, value)); // Minimum step of 1
  };

  const containerClass = `${styles.container} ${styles[theme]}`;
  const counterDisplayClass = `${styles.counterDisplay} ${
    count > 0 ? styles.positive : count < 0 ? styles.negative : styles.zero
  }`;

  return (
    <div className={containerClass}>
      <h1 className={styles.title}>ðŸ”¢ Counter Page</h1>
      <p className={styles.subtitle}>
        Demonstrating CSS Modules with scoped styling
      </p>

      <div className={styles.counterSection}>
        <div className={counterDisplayClass}>{count}</div>

        <div className={styles.controls}>
          <button
            className={`${styles.button} ${styles.decrementBtn}`}
            onClick={decrement}
          >
            âž– -{step}
          </button>

          <button
            className={`${styles.button} ${styles.resetBtn}`}
            onClick={reset}
          >
            ðŸ”„ Reset
          </button>

          <button
            className={`${styles.button} ${styles.incrementBtn}`}
            onClick={increment}
          >
            âž• +{step}
          </button>
        </div>
      </div>

      <div className={styles.stepSection}>
        <h3 className={styles.sectionTitle}>Step Control</h3>
        <div className={styles.stepControl}>
          <label htmlFor='stepInput' className={styles.stepLabel}>
            Increment/Decrement by:
          </label>
          <input
            id='stepInput'
            type='number'
            min='1'
            value={step}
            onChange={handleStepChange}
            className={styles.stepInput}
          />
        </div>
      </div>

      <div className={styles.historySection}>
        <h3 className={styles.sectionTitle}>Recent History</h3>
        <div className={styles.historyList}>
          {history.map((value, index) => (
            <span
              key={index}
              className={`${styles.historyItem} ${
                value > 0
                  ? styles.historyPositive
                  : value < 0
                    ? styles.historyNegative
                    : styles.historyZero
              }`}
            >
              {value}
            </span>
          ))}
        </div>
      </div>

      <div className={styles.infoSection}>
        <h3 className={styles.sectionTitle}>ðŸŽ¨ CSS Modules Demo</h3>
        <div className={styles.infoCard}>
          <h4>What are CSS Modules?</h4>
          <ul className={styles.infoList}>
            <li>
              âœ… <strong>Scoped CSS:</strong> Classes are automatically scoped
              to components
            </li>
            <li>
              âœ… <strong>No Conflicts:</strong> Same class names in different
              files won't conflict
            </li>
            <li>
              âœ… <strong>Import as Objects:</strong>{' '}
              <code>import styles from './file.module.css'</code>
            </li>
            <li>
              âœ… <strong>Dynamic Classes:</strong>{' '}
              <code>className={'{styles.myClass}'}</code>
            </li>
          </ul>
        </div>

        <div className={styles.codeExample}>
          <h4>Example Usage:</h4>
          <pre className={styles.code}>
            {`// Import CSS modules
import styles from './CounterPage.module.css';

// Use classes
<div className={styles.container}>
<button className={\`\${styles.button} \${styles.primary}\`}>
  Click me
</button>
</div>`}
          </pre>
        </div>
      </div>

      <div className={styles.featuresGrid}>
        <div className={styles.featureCard}>
          <div className={styles.featureIcon}>ðŸ”’</div>
          <h4>Scoped Styles</h4>
          <p>CSS classes are automatically scoped to prevent conflicts</p>
        </div>
        <div className={styles.featureCard}>
          <div className={styles.featureIcon}>ðŸŽ¯</div>
          <h4>Type Safety</h4>
          <p>IntelliSense support for CSS class names in TypeScript</p>
        </div>
        <div className={styles.featureCard}>
          <div className={styles.featureIcon}>ðŸ”„</div>
          <h4>Dynamic Classes</h4>
          <p>Easily combine and conditionally apply CSS classes</p>
        </div>
      </div>
    </div>
  );
};

export default CounterPage;
