import { useContext } from 'react';
import { ThemeContext } from '../provider/ThemeProvider';

export const useAppTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};
