import styles from './styles.module.css';
// import { ActivityIndicator } from './Components/Loader';

function App() {
  return (
    <>
      <section className={styles.wrapper}>
        {/* Header */}
        <div style={{ display: 'flex', flex: 0.3, backgroundColor: 'red' }}>
          <div
            style={{
              backgroundColor: 'white',
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-start',
              alignItems: 'center',
            }}
          >
            A
          </div>
          {/* <div
            style={{ backgroundColor: 'violet', display: 'flex', flex: 0.4 }}
          >
            B
          </div> */}
          <div
            style={{
              backgroundColor: 'beige',
              display: 'flex',
              flex: 0.3,
              justifyContent: 'flex-end',
              alignItems: 'center',
            }}
          >
            C
          </div>
        </div>
        {/* Body */}
        <div style={{ display: 'flex', flex: 1, backgroundColor: 'green' }}>
          <div
            style={{ display: 'flex', flex: 0.2, backgroundColor: 'orange' }}
          >
            Body 1
          </div>
          <div style={{ display: 'flex', flex: 0.8, backgroundColor: 'blue' }}>
            Body 2
          </div>
        </div>
        <div
          style={{
            display: 'flex',
            flex: 0.3,
            backgroundColor: 'teal',
            justifyContent: 'center',
          }}
        >
          Footer
        </div>
      </section>
    </>
  );
}

export default App;

